DROP TABLE IF EXISTS `gd_goods`;
CREATE TABLE `gd_goods` (
  `goods_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '商品ID',
  `cat_id` smallint(6) NOT NULL COMMENT '分类ID',
  `brand_id` int(11) DEFAULT NULL COMMENT '品牌ID',
  `sku_id` bigint(20) DEFAULT NULL COMMENT '默认skuId',
  `goods_type` smallint(6) NOT NULL COMMENT '商品类型ID',
  `goods_sn` varchar(60) NOT NULL COMMENT '商品货号',
  `goods_name` varchar(120) NOT NULL COMMENT '商品名称',
  `goods_name_style` varchar(60) DEFAULT NULL COMMENT '商品名称显示样式',
  `keywords` varchar(255) DEFAULT NULL COMMENT '商品关键词',
  `goods_img` varchar(255) DEFAULT NULL COMMENT '图片链接地址',
  `goods_imgs` varchar(500) DEFAULT NULL COMMENT '商品图片集',
  `is_real` tinyint(4) DEFAULT '1' COMMENT '0:虚拟下载使用,1:实体邮递,2:虚拟在线使用',
  `is_series_on` tinyint(4) DEFAULT '0' COMMENT '是否是系列',
  `is_on_sale` tinyint(4) DEFAULT '0' COMMENT '能否销售(待上架0、上架1、下架2、)',
  `is_alone_sale` tinyint(4) DEFAULT '1' COMMENT '能否单独销售',
  `is_shipping` tinyint(4) DEFAULT '1' COMMENT '是否需要配送',
  `integral` int(11) DEFAULT '0' COMMENT '商品的积分',
  `sort_order` smallint(6) DEFAULT '1' COMMENT '排列顺序',
  `is_delete` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否已删除',
  `is_best` tinyint(4) DEFAULT '0' COMMENT '是否精品',
  `is_new` tinyint(4) DEFAULT '0' COMMENT '是否新品',
  `is_hot` tinyint(4) DEFAULT '0' COMMENT '是否热销',
  `is_promote` tinyint(4) DEFAULT '0' COMMENT '是否特价',
  `is_topic` tinyint(4) DEFAULT '0' COMMENT '是否是专题商品',
  `topic_id` bigint(20) DEFAULT '0' COMMENT '专题ID',
  `supplier_id` int(11) DEFAULT '0' COMMENT '店铺ID',
  `is_check` tinyint(4) DEFAULT NULL COMMENT '供货商商品审核标识',
  `seller_note` varchar(255) DEFAULT NULL COMMENT '商家备注',
  `click_count` int(11) DEFAULT '0' COMMENT '浏览次数',
  `add_time` int(11) DEFAULT NULL COMMENT '添加时间',
  `update_time` datetime DEFAULT NULL COMMENT '最后更新时间',
  `tags` varchar(255) DEFAULT NULL COMMENT '标签',
  `goods_intro` varchar(1000) DEFAULT NULL COMMENT '商品简介',
  `favorites_num` int(11) DEFAULT '0' COMMENT '收藏数',
  `view_num` int(11) DEFAULT '0' COMMENT '浏览数',
  `relay_num` int(11) DEFAULT '0' COMMENT '转发数',
  `hot_num` int(11) DEFAULT '0' COMMENT '热度',
  PRIMARY KEY (`goods_id`,`is_delete`),
  KEY `sku_id` (`sku_id`),
  KEY `cat_id` (`cat_id`),
  KEY `brand_id` (`brand_id`),
  KEY `goods_type` (`goods_type`)
) ENGINE=MyISAM AUTO_INCREMENT=403 DEFAULT CHARSET=utf8;

