INSERT INTO `gd_goods` VALUES ('331', '460', null, '390', '18', 'r46', '接口互操作性仿真评估', '', '接口 操作性 仿真 评估', 'http://image.rongrong.cn/group1/M00/01/EA/CgMDF1tFWiaAZ1bHAAD_tZ0Pe7s644.png', '', '3', '0', '1', '0', '0', '50', '1', '0', '0', '0', '0', '0', '0', '0', '33', '1', '', '0', '1530613640', '2018-07-12 19:17:48', '', '目前，飞机/悬挂物综合均在主机单位以新型武器地面挂装形式开展，周期长、成本高。还存在即使顺利综合，仍不能保证通过在鉴定阶段开展的飞机/悬挂物接口标准符合性验证检测的问题。同时，新型作战飞机内埋弹舱，空间紧凑，易于发生机械干涉，挂架维修、武器挂装的可达性受限，挂架更换所需周期远大于外挂性挂架。', '1', '9', '0', '0');
