INSERT INTO `gd_goods` VALUES ('333', '460', null, '392', '18', 'r48', '企业信息编码标准体系建设咨询服务', '', '信息编码 标准 ', 'http://image.rongrong.cn/group1/M00/01/EA/CgMDF1tFWbWANzz_AAEIbmIEhT4179.png', '', '3', '0', '1', '0', '0', '30', '1', '0', '0', '0', '0', '0', '0', '0', '33', '1', '', '0', '1530613876', '2018-07-12 19:17:39', '', '信息编码体系是一个系统工程，贯穿于企业产品设计、工艺、生产制造、经营管理、售后服务等全程，它是企业信息化一项重要的基础性工作。企业要实现信息化，首当其充的问题就是进行信息编码，离开了信息编码，应用系统就难以进行实施，而信息编码的好坏关系到企业信息系统的长期运行质量和系统运行的寿命周期，所以要首先建立完整的企业信息编码标准体系。', '-1', '1', '0', '0');
